div.script:
var productImages_ = new Array();
var productImagesNames_ = new Array();
root = '';
function product_addImage_(){
    popupBrowser.parameters.set('type','url');
    popupBrowser.parameters.set('folder','');
    popupBrowser.parameters.set('action','product_addImage_callBack_');
    popupBrowser.parameters.set('element','0');
    popupBrowser.open();
}
function product_addImage_callBack_(image,element){
    for (oneImage in productImages_) {
        if (productImages_[oneImage] == image) {
            return true;
        }
    }
    cpt = productImages_.length;
    productImages_[cpt] = image;
    productImagesNames_[cpt] = "draggable_image__" + cpt;
    product_updateImages_();
}

function product_updateImages_(){
    separator = '';
    $('product_images_').innerHTML = "";
    $('product_images_input_').value = '';
    productImages_.each(function(image, index){
        id = productImagesNames_[index]
        $('product_images_').innerHTML += '';
        $('product_images_input_').value += separator + image;
        separator = '|';
    });
}

function product_removeImage_(image){
    cpt = 0;
    var newProductImages = new Array();
    var newProductImagesNames = new Array();
    productImages_.each(function(actualImage, index){
        if(index != image){
            newProductImages[cpt] = productImages_[index];
            newProductImagesNames[cpt] = productImagesNames_[index];
            cpt++;
        }
    });
    productImages_ = newProductImages;
    productImagesNames_ = newProductImagesNames;
    product_updateImages_();
}

    cpt = productImages_.length;
    productImagesNames_[cpt] = "draggable_image__" + cpt;
    productImages_[cpt] = '';

product_updateImages_();
