div.RENDER_ENDJS:
var intervenants = $$('.oneIntervenant');
var intervenants_count = intervenants.length;
var activeShownIntervenant = 0;
new PeriodicalExecuter(
    function(pe) {
        Effect.Fade(intervenants[activeShownIntervenant], { duration: 0.5 });
        activeShownIntervenant += 1;
        if(activeShownIntervenant == intervenants_count){
            activeShownIntervenant = 0;
        }
        Effect.Appear(intervenants[activeShownIntervenant], { duration: 0.5 });
    }, 
    5
);
